import React, { useState } from 'react'
import { Map, Marker, GoogleApiWrapper, InfoWindow } from 'google-maps-react';

const GoogleMap = (props) => {
    const [infoWindow, setInfoWindow] = useState(false);
    const [activeMarker, setActiveMarker] = useState({});
    const [selectedPlace, setSelectedPlace] = useState({});

    const coords = { lat: 10.3019, lng: -85.8411 };

    let onMarkerClick = ((props, marker, e) => {
        setSelectedPlace(props)
        setActiveMarker(marker)
        setInfoWindow(true)
    })

    let onMapClick = ((props) => {
        if(infoWindow) {
            setInfoWindow(false)
            setActiveMarker(null)
        }
    })

    return (
        <div style={{height: '60%'}}>
            <Map
                google={props.google}
                onClick={onMapClick}
                center={coords}
                zoom={15}
                initialCenter={coords}
            >
                <Marker
                    onClick={onMarkerClick}
                    title={'Tamarindo meeting spot'}
                    name={'Meet at the lifeguard tower on Tamarindo Beach.'}
                    position={coords} />
                <InfoWindow
                    marker={activeMarker}
                    visible={infoWindow}>
                    <div>
                        <h1>{selectedPlace.name}</h1>
                    </div>
                </InfoWindow>
            </Map>
        </div>
    )
}

export default GoogleApiWrapper({apiKey: 'AIzaSyCZCT3bmiMcc3TcYVE1UQTbf3suEJmgpa4'})(GoogleMap);
